jQuery(document).ready(function (a) {
	a("#slider_lite").swiperight(function () {
		a("#slider_lite").carousel("prev")
	});
	a("#slider_lite").swipeleft(function () {
		a("#slider_lite").carousel("next")
	});
	a("#slider").swiperight(function () {
		a("#slider").carousel("prev")
	});
	a("#slider").swipeleft(function () {
		a("#slider").carousel("next")
	})
});